        return (
            <React.Fragment key={item.title}>
                <div
                    className="title active"
                    onClick={() => onTitleClick(index)}>
                    <i className="dropdown icon"/>
                    {item.title}
                </div>
                <div className="content active">
                    <p>{item.content}</p>
                </div>
